mapper.insert:
	
		SELECT NVL(MAX(apr_no), 0) + 1
		FROM apr
	

	INSERT INTO apr
	        (
	            apr_no
	            ,doc_no
	            ,apr_sq
	            ,apr_line
	            ,apr_emp
	            ,apr_yn
	            ,apr_name
	        )
	  VALUES
	        (
	            #{aprNo}
	            ,#{docNo}
	            ,#{aprSq}
	            ,#{aprSq}
	            ,#{aprEmp}
	            ,'4D1d'
	            ,get_emptitle(#{aprEmp})
	        )

mapper.update:
{CALL
	go_apr(
		#{docNo, mode=IN, jdbcType=INTEGER},
		#{aprEmp, mode=IN, jdbcType=VARCHAR},
		#{result, mode=OUT, jdbcType=INTEGER}
	)
}
