mapper.insert:

	SELECT NVL(MAX(apr_no), 0) + 1
	FROM apr

INSERT INTO apr
        (
             apr_no
            ,doc_no
            ,cust_no
            ,apr_sq
            ,apr_line
            ,apr_emp
            ,apr_yn
            ,apr_name
        )
  VALUES
  
        (
             #{apr.aprNo}
            ,#{doc.docNo}
            ,#{doc.custNo}
            ,#{apr.aprSq}
            ,#{apr.aprSq}
            ,#{apr.aprEmp}
            ,'4D1d'
            ,get_emptitle(#{apr.aprEmp})
        )

mapper.update:
{CALL
	go_apr(
		#{docNo, mode=IN, jdbcType=INTEGER},
		#{aprEmp, mode=IN, jdbcType=VARCHAR},
		#{result, mode=OUT, jdbcType=VARCHAR}
	)
}

{CALL
	apr_ok(
		#{docNo, mode=IN, jdbcType=INTEGER},
		#{aprEmp, mode=IN, jdbcType=VARCHAR},
		#{result, mode=OUT, jdbcType=VARCHAR}	
	)
}

{CALL
	apr_ng(
		#{docNo, mode=IN, jdbcType=INTEGER},
		#{aprEmp, mode=IN, jdbcType=VARCHAR},
		#{reCmt, mode=IN, jdbcType=VARCHAR},
		#{result, mode=OUT, jdbcType=VARCHAR}	
	)
}
