            ,save_name
            ,upl_name
            ,file_ext
            ,file_size
            ,upl_emp      
        )
  VALUES
        (
         #{fileNo}
        ,#{docNo}
        ,#{saveName}
        ,#{uplName}
        ,#{fileExt}
        ,#{fileSize}
        ,#{uplEmp}
        )

mapper.update:
UPDATE doc
SET title = #{title}
    
    ,cntn = #{cntn}
    
    ,final_line = #{finalLine}
    ,draft_dt = sysdate
    ,temp_id = #{tempId}
WHERE doc_no = #{docNo}
