mapper.insert:


	SELECT NVL(MAX(prjt_id),0) +1 
	FROM prjt


INSERT ALL INTO prjt ( prjt_id
					,prjt_name
					,prjt_stat
					,prjt_start_dt
					,prjt_due_dt
					,smry
					,purpose
					,resp_mngr_id)
			VALUES (#{prjtId}
					,#{prjtName}
					,#{prjtStat}
					,#{prjtStartDt}
					,#{prjtDueDT}
					,#{smry}
					,#{purpose}
					,#{respMngrId})
INTO dept (dept_id
			,dept_name
			,cust_no
			, dept_loc
			, dept_mngr)
VALUES(#{deptId}
		,#{deptName}
		,#{custNo}
		,#{deptLoc}
		,#{deptMngr})
SELECT 1 FROM DUAL
