mapper.select:
SELECT r.* FROM (SELECT rownum rn, t.* FROM (SELECT prjt_id ,prjt_name ,prjt_start_dt ,prjt_due_dt ,purpose ,smry ,get_codename(prjt_stat) as prjt_stat FROM prjt
AND ${searchCondition} LIKE '%' || #{keyword} || '%'
AND prjt_stat LIKE #{prjtStat}
AND prjt_start_dt >= #{prjtStart} AND prjt_due_dt <= #{prjtDue}
AND prjt_start_dt >= #{prjtStart}
AND prjt_due_dt <= #{prjtDue}
ORDER BY ${prjtSort} DESC, prjt_id DESC) t WHERE rownum <= (#{page} * 10)) r WHERE r.rn > (#{page} -1) * 10

SELECT COUNT(*)
FROM prjt 



				AND ${searchCondition} LIKE '%' || #{keyword} || '%'


				AND prjt_stat LIKE #{prjtStat}



				AND prjt_start_dt >= #{prjtStart}  AND prjt_due_dt <= #{prjtDue}


				AND prjt_start_dt >= #{prjtStart}


				AND prjt_due_dt <= #{prjtDue}

mapper.insert:


	SELECT 'CPJ'||LPAD(NVL((MAX(TO_NUMBER(SUBSTR(prjt_id, -4)))),0) + 1, 4, '0') 
	FROM prjt

INSERT INTO prjt (prjt_id 
					,prjt_name 
					,prjt_stat 
					,prjt_start_dt 
					,prjt_due_dt 
					,smry 
					,purpose 
					,resp_mngr_id
					,cust_no)
			VALUES (#{prjtId}
					,#{prjtName}
					,#{prjtStat}
					,#{prjtStartDt}
					,#{prjtDueDt}
					,#{smry}
					,#{purpose}
					,#{respMngrId}
					,#{custNo})
