mapper.insert:
	
		SELECT NVL(MAX(task_no),0) +1 
		FROM   task_common
	
	INSERT ALL
	    INTO task_common
	    			(
						 task_no
						,task_name
						,task_purpose
						,task_cntn
						,task_start_dt
						,task_due_dt
						,task_type
						,standard_no
						,prjt_mat
						,dept_id
						,cust_no
	                 )
	      VALUES
	      		(
	                 #{taskNo}
	                ,#{taskName}
	                ,#{taskPurpose}
	                ,#{taskCntn}
	                ,#{taskStartDt}
	                ,#{taskDueDt}
	                ,'5A2a'
	                ,#{reguId}
	                ,'5B1b'
	                ,#{deptId}
	                ,#{custNo}
	            )
		
	    INTO task_emps
	    		(
                     task_no
                    ,task_emp_id
                    ,dept_id
                    ,dept_name
                    ,cmplt
                    ,cust_no
	            )
	      VALUES
	      		(
	                 #{taskNo}
	                ,#{emp.taskEmpId}
	                ,#{emp.deptId}
	                ,get_deptname(#{emp.deptId})
	                ,'5F2f'
	                ,#{custNo}
	            )
				    
	SELECT * FROM DUAL

mapper.update:
{CALL
	regu_emp_ok(
		#{taskNo, mode=IN, jdbcType=INTEGER},
		#{taskEmpId, mode=IN, jdbcType=VARCHAR},
		#{result, mode=OUT, jdbcType=VARCHAR}
	)
}
