mapper.insert:


		SELECT NVL(MAX(coop_co_no),0) +1 
		FROM coop_co

	INSERT INTO coop_co (
					coop_co_no
                     ,task_no 
                     ,co_name 
                     ,co_tel  
                     ,pic 
                     ,cust_addr 
                     ,cntn)
	VALUES ( #{coopCoNo}
			,#{taskNo}
			,#{coName}
			,#{coTel}
			,#{pic}
			,#{custAddr}
			,#{cntn}
	)

mapper.update:
UPDATE coop_co
SET

      co_name = #{coName}


    , co_tel = #{coTel}


    , pic = #{pic}


    , cust_addr = #{custAddr}


    , cntn = #{cntn}

WHERE coop_co_no =#{coopCoNo}
