mapper.insert:
	INSERT INTO doc_pto
	        (
	            doc_no
	            ,pto_type
	            ,pto_start_dt
	            ,pto_end_dt
	            ,reason
	        )
	  VALUES
	        (
	            #{docNo}
	            ,#{ptoType}
	            ,TO_DATE(#{ptoStartDt}, 'YYYY-MM-DD HH24:MI')
	            ,TO_DATE(#{ptoEndDt}, 'YYYY-MM-DD HH24:MI')
	            ,#{reason}
	        )

mapper.update:
UPDATE doc_pto
SET pto_type = #{ptoType}
    ,pto_start_dt = TO_DATE(#{ptoStartDt}, 'YYYY-MM-DD HH24:MI')
    ,pto_end_dt = TO_DATE(#{ptoEndDt}, 'YYYY-MM-DD HH24:MI')
    ,reason = #{reason}
WHERE doc_no = #{docNo}
